INSERT INTO `news` (`NewsID`, `Title`, `Description`, `Content`, `ImageBackground`, `Upload_at`, `AccountID`) VALUES
('25078614c6', 'Nàng béo Hàn Quốc gây sốt MXH vì nét xinh đầy đặn, vòng 1, vòng 3 ấn tượng lên tới 1m', 'Cô gái có biệt danh Roopretelcham từng được chú ý khắp các mạng xã hội nhờ vẻ \"khổng lồ\" nhưng quyến rũ. ', '', './assets/image/news/1636358893-23-thumbnail-width640height480.jpg', '2021-11-10 08:22:42', '1'),
('405aae28c7', 'Thấy thiếu nữ trên xe khác có cử chỉ lạ, lái xe bám theo, phát hiện bí mật rợn người', 'Một tài xế quyết định bám theo chiếc ô tô hơn 10 km để cung cấp thông tin cho cảnh sát, sau khi hiểu được cử chỉ bất thường của thiếu nữ trên xe.', '<p>H&atilde;ng CNN cuối tuần qua&nbsp;dẫn lời cảnh s&aacute;t Mỹ cho hay, một thiếu nữ đ&atilde; được giải cứu sau khi c&ocirc; ra &aacute;m hiệu bằng tay cầu cứu một t&agrave;i xế.</p>\r\n\r\n<p>Theo Văn ph&ograve;ng cảnh s&aacute;t trưởng quận Laurel, bang Kentucky, Mỹ, gia đ&igrave;nh thiếu nữ 16 tuổi ở th&agrave;nh phố Asheville, North Carolina, đ&atilde; tr&igrave;nh b&aacute;o về việc thiếu nữ mất t&iacute;ch h&ocirc;m 2/11. Hai ng&agrave;y sau, một t&agrave;i xế ở bang Kentucky đ&atilde; gọi cho đường d&acirc;y n&oacute;ng 911 để tr&igrave;nh b&aacute;o về việc ph&aacute;t hiện một c&ocirc; g&aacute;i ra hiệu cầu cứu.&nbsp;</p>\r\n\r\n<p>&quot;Người tr&igrave;nh b&aacute;o điều khiển xe ở ph&iacute;a sau chiếc &ocirc; t&ocirc; chở c&ocirc; g&aacute;i v&agrave; ph&aacute;t hiện c&ocirc; g&aacute;i trong xe đang ra dấu bằng tay ngụ &yacute; rằng: Bạo lực tại nh&agrave; - T&ocirc;i cần gi&uacute;p đỡ - Bạo lực tại nh&agrave;&quot;,&nbsp; theo th&ocirc;ng b&aacute;o từ Văn ph&ograve;ng cảnh s&aacute;t trưởng quận Laurel.&nbsp;</p>\r\n\r\n<p>Tổ chức từ thiện Canadian Women&#39;s Foundation cho biết, khi ai đ&oacute; d&ugrave;ng &quot;cử chỉ một tay&quot;, rất c&oacute; thể người đ&oacute; muốn &aacute;m chỉ rằng họ đang gặp nạn v&agrave; cần gi&uacute;p đỡ. &quot;Cử chỉ một tay&quot; như trường hợp của thiếu nữ &aacute;p dụng gồm c&aacute;c bước: giơ tay l&ecirc;n sao cho l&ograve;ng b&agrave;n tay hướng về ph&iacute;a người họ cầu cứu. Sau đ&oacute;, gập ng&oacute;n c&aacute;i sao cho ng&oacute;n n&agrave;y &aacute;p s&aacute;t v&agrave;o l&ograve;ng b&agrave;n tay. Cuối c&ugrave;ng, gập to&agrave;n bộ 4 ng&oacute;n c&ograve;n lại xuống để ch&uacute;ng &ocirc;m lấy ng&oacute;n c&aacute;i.&nbsp;</p>\r\n\r\n<p>Cảnh s&aacute;t chia sẻ với đ&agrave;i WYMT rằng, thiếu nữ đ&atilde; học được &quot;cử chỉ một tay&quot; tr&ecirc;n mạng x&atilde; hội. T&agrave;i xế gọi điện b&aacute;o cho 911 đ&atilde; b&aacute;m theo chiếc xe chở thiếu nữ hơn 10 km để cung cấp th&ocirc;ng tin ch&iacute;nh x&aacute;c cho cảnh s&aacute;t. Lực lượng chức năng sau đ&oacute; đ&atilde; chặn chiếc xe lại v&agrave; giải cứu thiếu nữ.&nbsp;</p>\r\n\r\n<p>Cảnh s&aacute;t cũng cho biết, nghi phạm ban đầu đưa thiếu nữ từ North Carolina tới Ohio, nơi c&oacute; người th&acirc;n của t&ecirc;n n&agrave;y. Tuy nhi&ecirc;n, cả hai kh&ocirc;ng được ch&agrave;o đ&oacute;n khi người th&acirc;n của nghi phạm ph&aacute;t hiện thiếu nữ mới 16 tuổi v&agrave; gia đ&igrave;nh nạn nh&acirc;n đ&atilde; b&aacute;o mất t&iacute;ch.&nbsp;</p>\r\n\r\n<p>&quot;Ch&uacute;ng t&ocirc;i kh&ocirc;ng r&otilde; nạn nh&acirc;n đ&atilde; ra dấu trong bao l&acirc;u v&agrave; với bao nhi&ecirc;u t&agrave;i xế, nhưng cuối c&ugrave;ng cũng c&oacute; người hiểu được&quot;, đ&agrave;i WYMT dẫn lời cảnh s&aacute;t địa phương.&nbsp;</p>\r\n\r\n<p>Nghi phạm bị bắt giữ h&ocirc;m 4/11 v&agrave; bị c&aacute;o buộc tội giam giữ bất hợp ph&aacute;p v&agrave; c&oacute; dấu hiệu x&acirc;m hại t&igrave;nh dục trẻ vị th&agrave;nh ni&ecirc;n.&nbsp;</p>\r\n', './assets/image/news/Bao-canh-sat-vi-thay-thieu-nu-tren-xe-khac-co-cu-chi-la-tai-xe-bat-ngo-lap-cong-1-1636357248-724-width1092height819.jpg', '2021-11-10 08:55:40', '1'),
('4b5689c198', 'Thêm 1 loại vắc-xin phòng COVID-19 được phê duyệt tại Việt Nam', 'Ngày 10/11 Bộ Y tế đã phê duyệt vắc xin Covaxin do Công ty Bharat Biotech International Limited (Ấn Độ) sản xuất. Trước đó, Tổ chức Y tế Thế giới (WHO) cũng đã ban hành sử dụng khẩn cấp (EUL) đối với vắc xin Covaxin (do Bharat Biotech - Ấn Độ phát triển), bổ sung vào danh mục vắc xin ngày càng tăng được WHO xác nhận để phòng ngừa COVID-19 do SARS-CoV- 2.', '<p>Mỗi liều&nbsp;<a href=\"https://www.24h.com.vn/vac-xin-covid-19-c46e6432.html\">vắc xin</a>&nbsp;c&oacute; 0,5 ml chứa 6 mcg kh&aacute;ng nguy&ecirc;n to&agrave;n&nbsp;<a href=\"https://www.24h.com.vn/tong-hop-so-lieu-dich-covid-19-c972.html\">SARS-CoV-2</a>&nbsp;bất hoạt (chủng NIV-2020-770), b&agrave;o chế ở dạng hỗn dịch ti&ecirc;m. Vắc xin được đ&oacute;ng g&oacute;i hộp 16 lọ, mỗi lọ chứa 1 liều, 5 liều, 10 liều v&agrave; 20 liều; mỗi liều 0,5 ml.</p>\r\n\r\n<p>Bộ Y tế cũng quy định c&aacute;c điều kiện đi k&egrave;m việc ph&ecirc; duyệt Covaxin cho nhu cầu cấp b&aacute;ch trong ph&ograve;ng, chống dịch bệnh COVID-19. Cụ thể, Cục Quản l&yacute; Dược c&oacute; tr&aacute;ch nhiệm cấp ph&eacute;p nhập khẩu vắc xin Covaxin theo quy định khi nhận được hồ sơ của cơ sở nhập khẩu. Cơ quan n&agrave;y cần thực hiện đ&uacute;ng quy định của ph&aacute;p luật về quản l&yacute; nhập khẩu, quản l&yacute; chất lượng vắc xin nhập khẩu.</p>\r\n\r\n<p>Cục Khoa học C&ocirc;ng nghệ v&agrave; Đ&agrave;o tạo c&oacute; tr&aacute;ch nhiệm lựa chọn đơn vị c&oacute; đủ điều kiện đ&aacute;nh gi&aacute; t&iacute;nh an to&agrave;n, hiệu quả của vắc xin Covaxin tr&ecirc;n cơ sở &yacute; kiến của Hội đồng Tư vấn sử dụng vắc xin, sinh phẩm y tế của Bộ Y tế (Hội đồng Tư vấn) trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n\r\n<p>Cục Y tế Dự ph&ograve;ng thực hiện c&aacute;c tr&aacute;ch nhiệm li&ecirc;n quan đến ti&ecirc;m chủng vắc xin Covaxin. Viện Kiểm định Quốc gia Vắc xin v&agrave; Sinh phẩm Y tế c&oacute; tr&aacute;ch nhiệm kiểm định v&agrave; cấp Giấy chứng nhận xuất xưởng l&ocirc; vắc xin Covaxin trước khi đưa ra sử dụng.</p>\r\n\r\n<p>Bộ Y tế cũng đề nghị đơn vị đề nghị ph&ecirc; duyệt vắc xin c&oacute; tr&aacute;ch nhiệm phản hồi kịp thời c&aacute;c y&ecirc;u cầu của bộ để bổ sung th&ecirc;m dữ liệu hoặc những y&ecirc;u cầu c&oacute; li&ecirc;n quan vắc xin Covaxin v&agrave; chủ động cung cấp, cập nhật th&ocirc;ng tin mới trong suốt qu&aacute; tr&igrave;nh ph&aacute;t triển sản phẩm.</p>\r\n\r\n<p>C&ocirc;ng ty n&agrave;y phải phối hợp với cơ sở sản xuất vắc xin đảm bảo c&aacute;c điều kiện sản xuất tại cơ sở sản xuất vắc xin Covaxin nhập khẩu v&agrave;o Việt Nam; đảm bảo an to&agrave;n, hiệu quả v&agrave; chất lượng của l&ocirc; vắc xin Covaxin nhập khẩu v&agrave;o Việt Nam.</p>\r\n\r\n<p>Đồng thời, phải phối hợp với đơn vị ph&acirc;n phối, sử dụng vắc xin Covaxin triển khai hệ thống cảnh gi&aacute;c dược to&agrave;n diện đối với loại vắc xin n&agrave;y tại Việt Nam theo quy định của ph&aacute;p luật.</p>\r\n\r\n<p>Phối hợp với c&aacute;c cơ quan quản l&yacute; (Cục Khoa học c&ocirc;ng nghệ v&agrave; Đ&agrave;o tạo, Viện Kiểm định Quốc gia vắc xin v&agrave; sinh phẩm y tế, cục Y tế dự ph&ograve;ng, Viện Vệ sinh dịch tễ Trung ương) v&agrave; đơn vị c&oacute; đủ điều kiện tổ chức thực hiện việc đ&aacute;nh gi&aacute; t&iacute;nh an to&agrave;n, hiệu quả của Covaxin; kiểm định c&aacute;c l&ocirc; vắc xin Covaxin trước khi đưa ra sử dụng; hướng dẫn việc bảo quản, ph&acirc;n phối v&agrave; sử dụng vắc xin cho c&aacute;c cơ sở ti&ecirc;m chủng.</p>\r\n\r\n<p>Hiện Việt Nam c&oacute; 9 loại&nbsp;<a href=\"https://www.24h.com.vn/vac-xin-covid-19-c46e6432.html\">vắc xin COVID-19</a>&nbsp;đ&atilde; được ph&ecirc; duyệt c&oacute; điều kiện cho nhu cầu cấp b&aacute;ch trong ph&ograve;ng chống dịch sử dụng gồm: AstraZeneca (do AstraZeneca sản xuất); SputnikV (Viện nghi&ecirc;n cứu Gamaleya); COVID-19 vắc xin Janssen (Johnson &amp; Johnson); Spikevax (COVID-19 Vắc xin Moderna); Comirnaty (Pfizer-BioNTech); Vero Cell (China National Biotec Group (CNBG)/Sinopharm); Hayat - Vax (CNBG); Abdala (C&ocirc;ng ty AICA Laboraries, Base Business Unit (BBU) AICA - Cuba) v&agrave; Covaxin (C&ocirc;ng ty Bharat Biotech International Limited - Ấn Độ).</p>\r\n', './assets/image/news/Them-1-loai-vac-xin-phong-COVID-19-duoc-phe-duyet-tai-Viet-Nam-1-1636531025-722-width715height402.jpeg', '2021-11-10 08:57:27', '1'),
('a4832bc844', 'Năm 2021 thu ngân sách tăng 7.200 tỷ, trong đó có gần 3.000 tỷ tiền thuê đất của ĐSQ Mỹ', 'Giải trình trước Quốc hội tại phiên thảo luận kinh tế - xã hội, Bộ trưởng Tài chính Hồ Đức Phớc lý giải việc năm nay khó khăn nhưng thu nội địa vẫn tăng lên.', '<p>Theo đ&oacute;, một trong những khoản thu đột biến trong năm nay l&agrave; 2.997 tỷ đồng tiền thu&ecirc; đất của đại sứ qu&aacute;n Mỹ.</p>\r\n\r\n<p>Cụ thể, Bộ trưởng T&agrave;i ch&iacute;nh cho biết năm nay, ng&acirc;n s&aacute;ch đ&atilde; thu tăng l&ecirc;n&nbsp;7.200 tỷ đồng&nbsp;từ ph&aacute;t sinh của năm 2020 trở về trước, như truy thu thuế nh&agrave; thầu của Formosa được 2.257 tỷ, xử l&yacute; thu ch&ecirc;nh lệch gi&aacute; kh&iacute; trong bao ti&ecirc;u sản phẩm 2.457 tỷ, thu ng&acirc;n s&aacute;ch đối với c&aacute;c khoản kh&aacute;c l&agrave; 2.500 tỷ.</p>\r\n\r\n<p>&quot;Đặc biệt ph&aacute;t sinh đột biến l&agrave; 2.997 tỷ tiền thu&ecirc; đất của Đại sứ qu&aacute;n Mỹ&quot;, Bộ trưởng n&oacute;i.</p>\r\n', './assets/image/news/test2.jpg', '2021-11-10 08:15:45', '1'),
('bc864b982f', 'Giá xăng tiếp tục tăng mạnh, phá đỉnh 7 năm qua kể từ 15 giờ chiều 10/11', 'Chiều ngày 10/11, Liên Bộ Công Thương - Tài chính điều chỉnh giá xăng dầu.', '<p>Theo số liệu từ Bộ C&ocirc;ng Thương,&nbsp;<a href=\"https://www.24h.com.vn/tang-gia-xang-c46e1783.html\">gi&aacute; xăng</a>&nbsp;dầu th&agrave;nh phẩm tr&ecirc;n thị trường Singapore t&iacute;nh đến ng&agrave;y 2/11 tăng khoảng 3 - 4% so với ng&agrave;y 26/10 l&ecirc;n khoảng 98 - 100 USD/th&ugrave;ng.</p>\r\n\r\n<p>Ch&iacute;nh v&igrave; vậy, Li&ecirc;n Bộ C&ocirc;ng thương- T&agrave;i ch&iacute;nh quyết định điều chỉnh&nbsp;gi&aacute; xăng dầu&nbsp;từ 15h chiều nay như sau:</p>\r\n\r\n<p>Xăng E5RON92 tăng 559&nbsp;đồng mỗi l&iacute;t;</p>\r\n\r\n<p>Xăng RON 95 tăng 658 đồng/l&iacute;t.</p>\r\n\r\n<p>Dầu diesel v&agrave; dầu hỏa giữ nguy&ecirc;n;</p>\r\n\r\n<p>Dầu mazut giảm 389 đồng/kg.</p>\r\n\r\n<p>Theo đ&oacute; sau điều chỉnh&nbsp;<a href=\"https://www.24h.com.vn/tang-gia-xang-c46e1783.html\">gi&aacute; xăng dầu</a>&nbsp;tr&ecirc;n thị trường như sau:</p>\r\n\r\n<p>Xăng E5RON92: kh&ocirc;ng cao hơn&nbsp;23.669&nbsp;đồng/l&iacute;t;</p>\r\n\r\n<p>Xăng RON 95 kh&ocirc;ng cao hơn&nbsp;24.996&nbsp;đồng/l&iacute;t,</p>\r\n\r\n<p>Dầu diesel kh&ocirc;ng cao hơn&nbsp;18.716 đồng/l&iacute;t;</p>\r\n\r\n<p>Dầu hỏa kh&ocirc;ng cao hơn&nbsp;17.637 đồng/l&iacute;t</p>\r\n\r\n<p>Dầu mazut kh&ocirc;ng cao hơn&nbsp; 16.821&nbsp;đồng/kg.</p>\r\n', './assets/image/news/Gia-xang-tang-manh-len-muc-cao-nhat-7-nam-qua-ke-tu-16-gio-chieu-26-10-2333-1636526187-150-width500height375.jpg', '2021-11-10 08:56:36', '1');

